    return (
        <Col xs={12} sm={3}>
            {/* <Panel bsStyle="primary">
                <Panel.Heading >
                    <Panel.Title componentClass="h3">
                        {props.title}
                    </Panel.Title>
                </Panel.Heading>
                <Panel.Body>
                    {props.body}
                </Panel.Body>
            </Panel> */}
                <FlippingCard>
        <FlippingCardBack>
  		  Content that will be displayed on the back of the card
  	  </FlippingCardBack>
  	  <FlippingCardFront>
  		  Content that will be displayed on the front of the card
  	  </FlippingCardFront>
  </FlippingCard>
        </Col>
    )
};

export default card;
