import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

import {
          Navbar,
          Nav,
          NavItem,
          Glyphicon,
          Modal,
          Button
     } from 'react-bootstrap';

import Sidebar from 'react-bootstrap-sidebar';

import classes from '../Navbar/style.css';

class NavBar extends Component {
  state={
      isVisible: true
  }

  onToggleSidebar = () => {
    this.setState(prevState => {
      this.state.isVisible = !prevState.isVisible
    });
    this.forceUpdate();
  }

  render(){
    return (
        <div>

          {/* Top navigation bar. */}

          <Navbar inverse fluid={true}>
            <Navbar.Collapse>
              <Nav>
                <NavItem eventKey={0} onClick={ () => this.onToggleSidebar() }>
                  <Glyphicon glyph="align-justify" />
                </NavItem>
              </Nav>
              <Nav pullRight>
                <NavItem eventKey={1} href="#">
                  Sign In
                </NavItem>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          
          {/* A sidebar like modal. */}

          <div>
                  <Button bsStyle="primary"><Glyphicon glyph="menu-hamburger"/></Button>
                  <Sidebar side='left' isVisible={ this.state.isVisible } onHide={ () => this.updateModal(false) }>
                    <Nav>
                      <NavItem href="#">Link 1</NavItem>
                      <NavItem href="#">Link 2</NavItem>
                      <NavItem href="#">Link 3</NavItem>
                      <NavItem href="#">Link 4</NavItem>
                    </Nav>
                  </Sidebar>
              </div>
      </div>
    );
    }
};

export default NavBar;
