    //         dispatch(fetchOverviewDataSuccess(getData()));
    //     }
    // }
    // catch(error){
    //     dispatch(fetchOverviewDataFail(error));
    // }

    return dispatch => {
        dispatch(fetchOverviewDataStart());
        axios.get('/sales.json')
            .then(res => {
                console.log(res.data);
            }).catch(err => console.log(err));
    }
}
