import React from 'react';

import { Panel } from 'react-bootstrap';

const alert = (props) => {
    return (
        <Panel bsStyle='danger' defaultExpanded hidden={props.show}>
            <Panel.Heading>
                <Panel.Title toggle>
                    {props.title}
                </Panel.Title>
            </Panel.Heading>
            <Panel.Collapse>
            <Panel.Body>
                <ul>
                    {props.messages.forEach(m => {
                        return <li></li>
                    })}
                </ul>
            </Panel.Body>
            </Panel.Collapse>
        </Panel>
    );
}

export default alert;
