import React from 'react';

const onRenderEditableCellHandler = (cellInfo) =>{
    return (
        <div
            style={{ backgroundColor: "#fafafa" }}
            contentEditable
            suppressContentEditableWarning
            onBlur={e => {
                const data = [...this.state.data];
                data[cellInfo.index][cellInfo.column.id] = e.target.innerHTML;
                // this.setState({ data });
            }}
            dangerouslySetInnerHTML={{
                // __html: this.state.data[cellInfo.index][cellInfo.column.id]
            }}
      />
    )
};

const manufacturerColumns = [
    {
        Header: 'Manufacturer',
        accessor: 'name', // String-based value accessors!
        Cell: onRenderEditableCellHandler
    }, 
    {
        Header: 'Name',
        accessor: 'age',
        Cell: onRenderEditableCellHandler // Custom cell components!
    }, 
    {
        Header: 'Phone', // Required because our accessor is not a string
        accessor: 'Friend Name',
        Cell: onRenderEditableCellHandler  // Custom value accessors!
    }, 
    {
        Header: 'Email', // Required because our accessor is not a string
        accessor: 'Friend Name',
        Cell: onRenderEditableCellHandler  // Custom value accessors!
    }, 
    {
        Header: 'Address',
        accessor: 'age',
        Cell: onRenderEditableCellHandler // Custom cell components!
    },
];

export default manufacturerColumns;
